import React, { useEffect, useRef, useState } from 'react';
import * as S from './ViewQuestionStyled';
import * as GS from '~/components/templates/Styled';
import { ThreeRowTemplate } from '~/components/templates/ThreeRowTemplate';

import Banner3d from 'Asset/image/banner/banner3d.png';
import ProfileDefault from 'Asset/image/profile_default.png';
import Fire from 'Asset/image/fire.png';
import ArrowBlackTop from 'Asset/image/arrow_black_top.png';
import ArrowBlackBottom from 'Asset/image/arrow_black_bottom.png';
import Js from 'Asset/image/category_js.png';
import Java from 'Asset/image/category_java.png';
import PhotoShop from 'Asset/image/category_photoshop.png';
import InDesign from 'Asset/image/category_indesign.png';
import Info from 'Asset/image/info.png';

import { useNavigate } from 'react-router';
import { Editor } from '~/pages/question/components/Editor';
import DeviceWidth from '~/model/constants/DeviceWidth';
import { Modal } from '~/components/modal/Modal';
import { checkSession } from '~/network/BaseService';
import { useParams } from 'react-router-dom';
import { QuestionService } from '~/network/service/QuestionService';

import { COACHING_WRITE_CONFIRM, openModal, modalEvent } from '~/store/ModalSlice';
import { useSelector, useDispatch } from 'react-redux';

export const ViewQuestion = ({ windowSize }) => {
  const dispatch = useDispatch();
  const params = useParams();

  const scrollRef = useRef();
  const [isLoading, setLoading] = React.useState(false);
  const [isCoachingWriteModal, setCoachingWriteModal] = React.useState(false);
  const [isCoachingSelectModal, setCoachingSelectModal] = React.useState(false);
  const [arrFoldIndex, setArrFoldIndex] = React.useState([]);
  const [arrOpenIndex, setArrOpenIndex] = React.useState([]);
  const [isOpenWrite, setOpenWrite] = React.useState(false);
  const [question, setQuestion] = React.useState();

  let arrAnswer = [
    {
      writer: '김코치',
      content: `아니 그걸 못해?<br/><p><img src="data:image/png;base64,"></p>`,
      time: '3주 전',
      selected: true,
      isOpen: true,
    },
  ];

  const navigate = useNavigate();

  const getQuestion = async () => {
    const res = await QuestionService.getQuestion({
      postId: params.postId,
    });
    if ('data' in res) {
      setQuestion(res.data);
    }
  };

  useEffect(() => {
    if (!checkSession()) {
      navigate('/', { replace: true });
    }

    getQuestion();

    var arr = [];
    for (var i = 0; i < arrAnswer.length; i++) {
      arr.push(i);
    }
    setArrFoldIndex(arr);

    var openArr = [0];
    setArrOpenIndex(openArr);

    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    if (isOpenWrite) {
      if (windowSize.width <= DeviceWidth.PHONE) {
        window.scroll(0, document.body.scrollHeight);
      } else {
        window.scroll(0, document.body.scrollHeight - 1060);
      }
    }
  }, [isOpenWrite]);

  return (
    <ThreeRowTemplate profileImg={ProfileDefault} hasFloating={false} isLoading={isLoading} windowSize={windowSize}>
      <S.Wrapper ref={scrollRef}>
        <GS.Banner3d>
          <GS.Banner3dImage src={Banner3d} />
        </GS.Banner3d>
        {question && (
          <S.Question>
            <S.QuestionHeader>
              <S.QuestionInfo>
                <S.CategoryItem src={Java}></S.CategoryItem>
                <S.QuestionPoint>{question.point}P</S.QuestionPoint>
              </S.QuestionInfo>
              <S.QuestionTitle>{question.title}</S.QuestionTitle>
              <S.QuestionWriterProfile src={ProfileDefault} />
              <S.QuestionWriterNickname>{question.nickName}</S.QuestionWriterNickname>
              <S.QuestionTime>{question.createdAt.substring(0, 16)}</S.QuestionTime>
              <S.QuestionCoachingWrapper>
                <S.QuestionCoaching hasNoFire={!question.fire || question.fire == 0}>
                  코칭 {question.replies.length}
                </S.QuestionCoaching>
                {/* <GS.Container style={{ display: 'inline' }}>
                  {new Array(question.fire).fill(0).map((fire, i) => {
                    return <S.QuestionFire key={i} src={Fire}></S.QuestionFire>;
                  })}
                </GS.Container> */}
                <S.QuestionMenu src={Info} />
              </S.QuestionCoachingWrapper>
            </S.QuestionHeader>

            <GS.Divider width="2px" style={{ margin: '18px 0 32px 0' }} />

            <S.ContentWrapper isOpen={true}>
              <div spellCheck={false} dangerouslySetInnerHTML={{ __html: question.content }} />
            </S.ContentWrapper>

            {arrAnswer.map((answer, i) => {
              return (
                <GS.Container key={i}>
                  <GS.Divider width="2px" style={{ marginTop: '84px' }} />
                  <S.AnswerFold
                    onClick={() => {
                      if (arrFoldIndex.indexOf(i) != -1) {
                        setArrFoldIndex(arrFoldIndex.filter((index) => index !== i));
                      } else {
                        setArrFoldIndex([...arrFoldIndex, i]);
                      }
                    }}>
                    <S.AnswerTitle>코칭 {i + 1}.</S.AnswerTitle>
                    {answer.selected && <S.AnswerCoaching>코칭채택</S.AnswerCoaching>}
                    <GS.FR>
                      <S.AnswerArrow src={ArrowBlackTop} />
                    </GS.FR>
                  </S.AnswerFold>
                  {arrFoldIndex.find((e) => e == i) >= 0 && (
                    <GS.Container>
                      <S.AnswerHeader>
                        <S.AnswerWriterProfile src={ProfileDefault} />
                        <S.AnswerWriterNickname>{answer.writer}</S.AnswerWriterNickname>
                        <S.AnswerTime>{answer.time}</S.AnswerTime>
                        <S.QuestionCoachingWrapper>
                          <S.AnswerMenu src={Info} />
                        </S.QuestionCoachingWrapper>
                      </S.AnswerHeader>
                      <GS.Divider width="2px" style={{ margin: '18px 0 32px 0' }} />
                      <S.ContentWrapper isOpen={arrOpenIndex.indexOf(i) != -1}>
                        <div spellCheck={false} dangerouslySetInnerHTML={{ __html: answer.content }} />
                      </S.ContentWrapper>
                      {i != arrAnswer.length - 1 && arrOpenIndex.indexOf(i) > -1 && (
                        <GS.Container>
                          <GS.FR>
                            <S.CoachingSelectBtn
                              onClick={() => {
                                modalEvent.ok = null;
                                dispatch(
                                  openModal({
                                    type: COACHING_CHOICE,
                                    modalContent: {
                                      title: `코칭이 도움이 되셨나요?\n얼마나 도움이 되셨는지 체크 후에 확인을 눌러주세요!`,
                                      img: Banner3d,
                                    },
                                  }),
                                );
                              }}>
                              {' '}
                              채택하기<S.CoachingSelectBtnPointText>200P</S.CoachingSelectBtnPointText>
                            </S.CoachingSelectBtn>
                          </GS.FR>
                        </GS.Container>
                      )}
                      {arrOpenIndex.indexOf(i) == -1 && (
                        <GS.Container
                          onClick={() => {
                            if (arrOpenIndex.indexOf(i) == -1) {
                              setArrOpenIndex([...arrOpenIndex, i]);
                            }
                          }}
                          style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%,-50%)' }}>
                          <S.OpenBtn />
                          <S.OpenText>오픈하기</S.OpenText>
                          <S.OpenPointText>10P</S.OpenPointText>
                        </GS.Container>
                      )}
                    </GS.Container>
                  )}
                </GS.Container>
              );
            })}

            {isOpenWrite && (
              <GS.Container style={{ marginTop: '94px' }}>
                <Editor windowSize={windowSize} />
              </GS.Container>
            )}
            <S.CoachingBtn
              style={{
                marginTop: arrFoldIndex.indexOf(arrAnswer.length - 1) < 0 && !isOpenWrite ? '44px' : '0px',
              }}
              onClick={() => {
                if (isOpenWrite) {
                  modalEvent.ok = null;
                  dispatch(
                    openModal({
                      type: COACHING_WRITE_CONFIRM,
                      modalContent: { title: `정말 코칭에 참여하시나요?`, img: Banner3d },
                    }),
                  );
                } else {
                  setOpenWrite(true);
                }
              }}>
              {!isOpenWrite ? `코칭 추가` : `코칭 등록`}
            </S.CoachingBtn>
            {isOpenWrite && (
              <S.CoachingBtn
                style={{ backgroundColor: '#000', marginRight: '3vw' }}
                onClick={() => {
                  setOpenWrite(false);
                }}>
                취소
              </S.CoachingBtn>
            )}
          </S.Question>
        )}
      </S.Wrapper>
    </ThreeRowTemplate>
  );
};
